var wrapper=document.getElementById("signature-pad");var clearButton=wrapper.querySelector("[data-action=clear]");var changeColorButton=wrapper.querySelector("[data-action=change-color]");var undoButton=wrapper.querySelector("[data-action=undo]");var savePNGButton=wrapper.querySelector("[data-action=save-png]");var saveJPGButton=wrapper.querySelector("[data-action=save-jpg]");var saveSVGButton=wrapper.querySelector("[data-action=save-svg]");var canvas=wrapper.querySelector("canvas");var signaturePad=new SignaturePad(canvas,{backgroundColor:"rgb(255, 255, 255)"});function resizeCanvas(){var ratio=Math.max(window.devicePixelRatio||1,1);canvas.width=canvas.offsetWidth*ratio;canvas.height=canvas.offsetHeight*ratio;canvas.getContext("2d").scale(ratio,ratio);signaturePad.clear()}window.onresize=resizeCanvas;resizeCanvas();function sendToUrl(dataURL,url){var blob=dataURLToBlob(dataURL);var reader=new FileReader;reader.readAsDataURL(blob);reader.onloadend=function(){base64data=reader.result;$.post(url,{signature:base64data},function(data){console.log(data)})}}function download(dataURL,filename){var blob=dataURLToBlob(dataURL);var url=window.URL.createObjectURL(blob);var a=document.createElement("a");a.style="display: none";a.href=url;a.download=filename;document.body.appendChild(a);a.click();window.URL.revokeObjectURL(url)}function dataURLToBlob(dataURL){var parts=dataURL.split(";base64,");var contentType=parts[0].split(":")[1];var raw=window.atob(parts[1]);var rawLength=raw.length;var uInt8Array=new Uint8Array(rawLength);for(var i=0;i<rawLength;++i){uInt8Array[i]=raw.charCodeAt(i)}return new Blob([uInt8Array],{type:contentType})}if(clearButton!==null)clearButton.addEventListener("click",function(event){signaturePad.clear()});if(undoButton!==null)undoButton.addEventListener("click",function(event){var data=signaturePad.toData();if(data){data.pop();signaturePad.fromData(data)}});if(changeColorButton!==null)changeColorButton.addEventListener("click",function(event){var r=Math.round(Math.random()*255);var g=Math.round(Math.random()*255);var b=Math.round(Math.random()*255);var color="rgb("+r+","+g+","+b+")";signaturePad.penColor=color});if(savePNGButton!==null)savePNGButton.addEventListener("click",function(event){if(signaturePad.isEmpty()){alert("Please provide a signature first.")}else{var dataURL=signaturePad.toDataURL();download(dataURL,"signature.png")}});if(saveJPGButton!==null)saveJPGButton.addEventListener("click",function(event){if(signaturePad.isEmpty()){alert("Please provide a signature first.")}else{var dataURL=signaturePad.toDataURL("image/jpeg");download(dataURL,"signature.jpg")}});if(saveSVGButton!==null)saveSVGButton.addEventListener("click",function(event){if(signaturePad.isEmpty()){alert("Please provide a signature first.")}else{var dataURL=signaturePad.toDataURL("image/svg+xml");download(dataURL,"signature.svg")}});function saveToServer(url){if(signaturePad.isEmpty()){alert("Please provide a signature first.")}else{var dataURL=signaturePad.toDataURL();sendToUrl(dataURL,url)}}